import {bootstrapApplication} from '@angular/platform-browser';

import {AppComponent} from './app/app.component';
import {provideStore} from '@ngrx/store';
import {counterReducer} from "./app/store/counter/counter.reducer";
import {provideEffects} from '@ngrx/effects';
import {CounterEffects} from "./app/store/counter/counter.effects";
import {provideAnimations} from '@angular/platform-browser/animations';
import {mealsReducer} from "./app/store/meals/meals.reducer.";
import {MealsEffects} from "./app/store/meals/meals.effects";

bootstrapApplication(AppComponent, {
  providers: [
    provideStore({
      counterReducer,
      meals: mealsReducer
    }),
    provideEffects([CounterEffects, MealsEffects]), provideAnimations()],
  importProvidersFrom(HttpClientModule),
});
