import {Component, Input} from '@angular/core';
import {CommonModule} from '@angular/common';
import {MatCardModule} from "@angular/material/card";
import {MatButtonModule} from "@angular/material/button";
import {IMeal} from "../store/meals/meals.reducer.";

@Component({
  selector: 'app-meal-list-item',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule],
  templateUrl: './meal-list-item.component.html',
  styleUrls: ['./meal-list-item.component.css']
})
export class FoodListItemComponent {
  @Input() meal!: IMeal
}
<mat-card class="example-card">
  <img mat-card-image [src]="'http://localhost:3000/'+ meal.image" alt="Photo of a Shiba Inu">
  <mat-card-content>
    <mat-card-title class="fw-bold py-2">{{ meal.name }}</mat-card-title>

    <p>
      {{ meal.description }}
    </p>
  </mat-card-content>
  <mat-card-actions class="d-flex justify-content-between px-3">
    {{ meal.price + "$" }}
    <button class="ms-2" color="accent" mat-flat-button>To cart</button>
  </mat-card-actions>
</mat-card>
