import {Component, OnInit} from '@angular/core';
import {CommonModule} from '@angular/common';
import {Store} from "@ngrx/store";
import {IMeal} from "../store/meals/meals.reducer.";
import {updateMeals} from "../store/meals/meals.actions";
import get from "axios";
import {Observable} from "rxjs";

@Component({
  selector: 'app-meal-list',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './meal-list.component.html',
  styleUrls: ['./meal-list.component.css']
})
export class MealListComponent implements OnInit {
  meals$: Observable<IMeal[]>

  constructor(private store: Store<{ meals: IMeal[] }>) {
    this.meals$ = this.store.select("meals")
  }

  async ngOnInit() {
    const resp = await get("http://localhost:3000/meals")
    this.store.dispatch(updateMeals({value: resp.data}))

  }

}
