import {Actions, createEffect, ofType} from "@ngrx/effects";
import {decrement, increment, init, set} from "./counter.action";
import {of, switchMap, tap, withLatestFrom} from "rxjs";
import {Injectable} from "@angular/core";
import {Store} from "@ngrx/store";

@Injectable()
export class CounterEffects {
  loadCount = createEffect(() => this.actions$.pipe(
      ofType(init),
      tap(() => {

      })
      // switchMap(() => {
      //   const storedCounter = localStorage.getItem("counter")
      //   if (!storedCounter) {
      //     return of(set({value: 0}))
      //   }
      //   return of(set({value: +storedCounter}))
      // })
    )
  )


  saveCount = createEffect(() => this.actions$.pipe(
    ofType(increment, decrement),
    withLatestFrom(this.store.select("counterReducer")),
    tap(([action, store]) => {
      console.log(store)
      localStorage.setItem("counter", store.toString())
    })
  ), {dispatch: false})

  constructor(private actions$: Actions, private store: Store<{ counterReducer: number }>) {
  }
}
