import {Component, Input, OnInit} from '@angular/core';
import {CommonModule} from '@angular/common';
import {MatCardModule} from "@angular/material/card";
import {MatButtonModule} from "@angular/material/button";
import {IMeal} from "../store/meals/meals.reducer.";
import {RouterLink} from "@angular/router";
import {ICart, ICartStore} from "../store/cart/cart.reducer";
import {Store} from "@ngrx/store";
import {updateCart} from "../store/cart/cart.actions";

@Component({
  selector: 'app-meal-list-item',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule, RouterLink],
  templateUrl: './meal-list-item.component.html',
  styleUrls: ['./meal-list-item.component.css']
})
export class FoodListItemComponent implements OnInit {
  @Input() meal!: IMeal

  constructor(private store: Store) {
  }

  ngOnInit() {

  }

  onAddToCart(cartItem: ICart) {
    this.store.dispatch(updateCart({value: cartItem}))
  }
}
