import {createAction, props} from "@ngrx/store";
import {IMeal} from "./meals.reducer.";

export enum MEALS_ACTIONS {
  INIT = "[Meals] init",
  UPDATE = "[Meals] updateMeals"

}

export const init = createAction("[Meals] init")

export const updateMeals = createAction(
  "[Meals] updateMeals",
  props<{ value: IMeal[] }>()
)
