import {Component, OnDestroy} from '@angular/core';
import {CommonModule} from '@angular/common';
import {Store} from '@ngrx/store';
import {ICart} from '../../store/cart/cart.reducer';
import {selectCartData} from '../../store/cart/meals.selectors';
import {MatListModule} from '@angular/material/list';
import {MatIconModule} from '@angular/material/icon';
import {MatButtonModule} from '@angular/material/button';
import {addToCart, removeFromCart} from '../../store/cart/cart.actions';
import {Observable, Subscription} from 'rxjs';
import {takeUntil} from 'rxjs/operators';
import {BaseContainerComponent} from "../../base-container/base-container.component";

@Component({
  selector: 'app-cart-page',
  standalone: true,
  imports: [CommonModule, MatListModule, MatIconModule, MatButtonModule, BaseContainerComponent],
  templateUrl: './cart-page.component.html',
})
export class CartPageComponent {
  cart$: Observable<ICart[]> = this.store.select(selectCartData);

  constructor(private store: Store) {
  }


  onAddToCart(id: string) {
    this.store.dispatch(addToCart({value: id}));
  }

  onRemoveFromCart(id: string) {
    this.store.dispatch(removeFromCart({value: id}));
  }
}

<div *ngIf="cart$|async">
  <mat-list role="list">
    <div class="d-flex align-items-center" *ngFor="let cartItem of cart$|async" role="listitem"
    >{{ "ID: " + cartItem.id }}
      <button (click)="onRemoveFromCart(cartItem.id)" class="d-flex align-items-center ms-3" mat-icon-button>
        <mat-icon>remove</mat-icon>
      </button>
      <div class="mx-3"> {{ cartItem.count }}</div>
      <button (click)="onAddToCart(cartItem.id)" class="d-flex align-items-center " mat-icon-button>
        <mat-icon>add</mat-icon>
      </button>
    </div>
  </mat-list>
  <button mat-flat-button color="primary">Order</button>

  <app-base-container [template]="loginTemplate"/>

  <ng-template #loginTemplate>
    <div class="pt-3"> Please
      <button mat-flat-button color="primary">Login</button>
      to create order
    </div>
  </ng-template>
  asddads
</div>
<app-base-container [template]="noItemsTemplate"/>
<ng-template #noItemsTemplate>
  <div *ngIf="!(cart$|async)?.length">No items in cart</div>
</ng-template>
