import {Component, OnDestroy, OnInit} from '@angular/core';
import {count, Observable, Subscription} from 'rxjs';

import {CounterService} from '../counter.service';
import {Store} from "@ngrx/store";
import {AsyncPipe} from "@angular/common";

@Component({
  selector: 'app-counter-output',
  templateUrl: './counter-output.component.html',
  styleUrls: ['./counter-output.component.css'],
  standalone: true,
  imports: [
    AsyncPipe
  ]
})
export class CounterOutputComponent {
  count$: Observable<number>
  double: number

  constructor(private store: Store<{ counterReducer: number }>) {
    this.count$ = store.select("counterReducer")
    this.count$.subscribe(async (val) => {
      this.double = val * 2

      //console.log(val)
    })
  }


}
<p class="counter">{{ count$ | async }}</p>
<p class="counter">Double: </p>
