import {Component, OnInit} from '@angular/core';
import {CommonModule} from '@angular/common';
import {Store} from "@ngrx/store";
import {IMeal} from "../store/meals/meals.reducer.";
import {Observable} from "rxjs";
import {init} from "../store/meals/meals.actions";
import {selectMealsData} from "../store/meals/meals.selectors";
import {MealsService} from "../services/MealsService";

@Component({
  selector: 'app-meal-list',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './meal-list.component.html',
  styleUrls: ['./meal-list.component.css']
})
export class MealListComponent implements OnInit {
  meals$: Observable<IMeal[]>

  constructor(private store: Store<{ meals: IMeal[] }>, private mealsService: MealsService) {
    this.meals$ = this.store.select(selectMealsData)

    this.meals$.subscribe((val) => {
      console.log(1, val)
    })
    //console.log(this.meals$)
  }

  async ngOnInit() {
    // this.store.dispatch(init())
    this.mealsService.loadMeals()
  }

}
<ul>
  <li *ngFor="let meal of meals$">{{ meal.description }}</li>
</ul>
