import {Component} from '@angular/core';
import {MatIconModule} from "@angular/material/icon";
import {MatButtonModule} from "@angular/material/button";
import {RouterLink} from "@angular/router";
import {Store} from "@ngrx/store";
import {selectCartData} from "../store/cart/meals.selectors";

@Component({
  selector: 'app-header',
  templateUrl: './app-header.component.html',
  styleUrls: ['./app-header.component.css'],
  imports: [MatIconModule, MatButtonModule, RouterLink],
  standalone: true
})
export class AppHeaderComponent {
  itemsCount = 0

  constructor(private store: Store) {
    this.store.select(selectCartData).subscribe((val) => {
      this.itemsCount = val.reduce((acc, item) => acc + item.count, 0)
    })
  }
}
<header class="d-flex justify-content-between container align-items-center ">

  <button mat-button role="button" routerLink="/" class="d-flex align-items-center">
    Home food

    <mat-icon class="mx-2" aria-hidden="false" aria-label="Example home icon" fontIcon="home"/>

  </button>
  <div class="position-relative">
    <button routerLink="cart" class="d-flex align-items-center" mat-icon-button>
      <mat-icon>shopping_cart</mat-icon>
      <div class="position-absolute"> {{ itemsCount }}</div>

    </button>
  </div>

</header>
