import {Component, Input, TemplateRef} from '@angular/core';
import {CommonModule, NgTemplateOutlet} from '@angular/common';

@Component({
  selector: 'app-base-content-center',
  standalone: true,
  imports: [CommonModule, NgTemplateOutlet],
  templateUrl: './base-content-center.component.html',
})
export class BaseContentCenterComponent {
  @Input() template!: TemplateRef<any>
}
<section class="d-flex justify-content-center align-items-center">
  <ng-container *ngTemplateOutlet="template || defaultTemplate"></ng-container>
</section>

<ng-template #defaultTemplate>
  <div>Some default text</div>
</ng-template>
