import {Component, Input} from '@angular/core';
import {CommonModule} from '@angular/common';
import {MatCardModule} from "@angular/material/card";
import {MatButtonModule} from "@angular/material/button";
import {IMeal} from "../store/meals/meals.reducer.";

@Component({
  selector: 'app-meal-list-item',
  standalone: true,
  imports: [CommonModule, MatCardModule, MatButtonModule],
  templateUrl: './meal-list-item.component.html',
  styleUrls: ['./meal-list-item.component.css']
})
export class FoodListItemComponent {
  @Input() meal!: IMeal
}
<mat-card class="example-card">
  <mat-card-header>
    <div mat-card-avatar class="example-header-image"></div>
    <mat-card-title>{{ meal.name }}</mat-card-title>
    <mat-card-subtitle>{{ meal.price }}</mat-card-subtitle>
  </mat-card-header>
  <img mat-card-image [src]="'localhost:3000/'+ meal.image" alt="Photo of a Shiba Inu">
  <mat-card-content>
    <p>
      {{ meal.description }}
    </p>
  </mat-card-content>
  <mat-card-actions>
    <button mat-button>To cart</button>
    <button mat-button>SHARE</button>
  </mat-card-actions>
</mat-card>
