import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {IMeal} from "../store/meals/meals.reducer.";
import {Store} from "@ngrx/store";
import {selectMealById} from "../store/meals/meals.selectors";

@Component({
  selector: 'app-meal-page',
  templateUrl: './meal-page.component.html',
  styleUrls: ['./meal-page.component.css'],
  standalone: true
})
export class MealPageComponent implements OnInit {
  currentMeal: IMeal = {id: "", description: "", image: "", name: "", price: ""}

  constructor(private route: ActivatedRoute, private store: Store) {
  }

  ngOnInit() {
    const mealId = this.route.snapshot.params['id']
    this.store.select(selectMealById(mealId)).subscribe((meal) => {
      if (!meal) {
        return
      }

      this.currentMeal = meal
      //TODO: unsubscribe?
    })


  }
}
