import {Component} from '@angular/core';
import {CommonModule} from '@angular/common';
import {Store} from "@ngrx/store";
import {IMeal} from "../../store/meals/meals.reducer.";
import {Observable} from "rxjs";
import {selectMealsData} from "../../store/meals/meals.selectors";
import {FoodListItemComponent} from "../../meal-list-item/meal-list-item.component";

@Component({
  selector: 'app-meals-list-page',
  standalone: true,
  imports: [CommonModule, FoodListItemComponent],
  templateUrl: './meals-list-page.component.html',
})
export class MealsListPageComponent {
  meals$: Observable<IMeal[]>
  isMealsLoading$: Observable<boolean>

  constructor(private store: Store<{ meals: IMeal[] }>,) {
    this.meals$ = this.store.select(selectMealsData)
  }


}
<ul ngSwitch class="d-flex flex-wrap container px-0 pb-3">
  <div *ngSwitchCase="meals$|async ">
    <app-meals-list-page-item
      *ngFor="let meal of meals$ | async"
      [meal]="meal" class="w-25 px-2 mt-3"/>
  </div>

</ul>
