import {Component, OnInit} from '@angular/core';
import {CommonModule} from '@angular/common';
import {Store} from "@ngrx/store";
import {Observable} from "rxjs";
import {ICart} from "../../store/cart/cart.reducer";
import {selectCartData} from "../../store/cart/meals.selectors";
import {MatListModule} from "@angular/material/list";
import {MatIconModule} from "@angular/material/icon";
import {MatButtonModule} from "@angular/material/button";

@Component({
  selector: 'app-cart-page',
  standalone: true,
  imports: [CommonModule, MatListModule, MatIconModule, MatButtonModule],
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.css']
})
export class CartPageComponent {
  cart$: ICart[] = []

  constructor(private store: Store) {
    this.store.select(selectCartData).subscribe((val) => {
      this.cart$ = val
    })
  }


}

//TODO remove redundant css
<div class="container">
  <mat-list role="list">
    <div class="d-flex align-items-center" *ngFor="let cartItem of cart$" role="listitem"
    >{{ cartItem.id + " " + cartItem.count }}
      <button class="d-flex align-items-center ms-5" mat-icon-button>
        <mat-icon>remove</mat-icon>
      </button>
    </div>

  </mat-list>
</div>
