import {Component, OnInit} from '@angular/core';

import {CounterOutputComponent} from './counter-output/counter-output.component';
import {CounterControlsComponent} from './counter-controls/counter-controls.component';
import {Store} from "@ngrx/store";
import {init} from "./store/counter/counter.action";
import {AppHeaderComponent} from "./app-header/app-header.component";
import {MealListComponent} from "./meal-list/meal-list.component";
import {RouterOutlet} from "@angular/router";
import {MealsService} from "./services/MealsService";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
  standalone: true,
  imports: [CounterOutputComponent, CounterControlsComponent, AppHeaderComponent, MealListComponent, RouterOutlet],
})
export class AppComponent implements OnInit {
  constructor(private store: Store, private mealsService: MealsService) {
  }

  ngOnInit() {

    this.mealsService.loadMeals()

  }
}
