import {createReducer, on} from "@ngrx/store";
import {setIsMealsLoading, updateMeals} from "./meals.actions";

export interface IMeal {
  id: string,
  name: string,
  price: string,
  description: string,
  image: string
}

export interface IMealStore {
  isLoading: boolean,
  meals: IMeal[]
}

const initialState: IMealStore = {
  meals: [],
  isLoading: false
}
export const mealsReducer = createReducer(initialState,
  on(updateMeals, (state, action) => {
    //TODO console.log(action)
    return {...state, meals: action.value}
  }),
  on(setMealsLoading, (state, action) => {
    // console.log(action)
    return {...state, meals: action.value}
  })
)
