import {createReducer, on} from "@ngrx/store";
import {updateMeals} from "./meals.actions";

export interface IMeal {
  id: string,
  name: string,
  price: string,
  description: string,
  image: string
}

const initialState: IMeal[] = []
export const mealsReducer = createReducer(initialState,
  on(updateMeals, (state, action) => {
    return action.value
  })
)
