import {createAction, props} from "@ngrx/store";
import {IMeal} from "./meals.reducer.";

export const updateMeals = createAction(
  "[Meals] updateMeals",
  props<{ value: IMeal[] }>()
)

export const loadMeals = createAction(
  "[Meals] loadMeals",
  props<{ value: IMeal[] }>()
)
